function background(e,r){return{background:"-moz-linear-gradient(15deg, "+e+" 50%, "+r+" 50.1%)",background:"-o-linear-gradient(15deg, "+e+", "+r+" 50.1%)",background:"-webkit-linear-gradient(15deg, "+e+" 50%, "+r+")",background:"-ms-linear-gradient(15deg, "+e+" 50%, "+r+" 50.1%)",background:"linear-gradient(15deg, "+e+" 50%,"+r+" 50.1%)"}}function changeBg(e,r){$("div.bg").css(background(e,r)).fadeIn(700,function(){$("body").css(background(e,r)),$("bg").hide()})}var $slider=$(".slider");$slider.slick({arrows:!1,dots:!0,infinite:!0,speed:600,fade:!0,focusOnSelect:!0,customPaging:function(e,r){var n=$(e.$slides[r]).data("color").split(",")[1];return'<a><svg width="100%" height="100%" viewBox="0 0 16 16"><circle cx="8" cy="8" r="6.215" stroke="'+n+'"></circle></svg><span style="background:'+n+'"></span></a>'}}).on("beforeChange",function(e,r,n,a){var i=$("figure",$slider).eq(a).data("color").split(","),d=i[0],c=i[1];$(".price, .btn").css({color:d}),changeBg(d,c),$(".btn").css({borderColor:c})});