function background(e,n){return{background:"-moz-linear-gradient(15deg, "+e+" 50%, "+n+" 50.1%)",background:"-o-linear-gradient(15deg, "+e+", "+n+" 50.1%)",background:"-webkit-linear-gradient(15deg, "+e+" 50%, "+n+")",background:"-ms-linear-gradient(15deg, "+e+" 50%, "+n+" 50.1%)",background:"linear-gradient(15deg, "+e+" 50%,"+n+" 50.1%)"}}function changeBg(e,n){$("div.bg").css(background(e,n)).fadeIn(700,function(){$("body").css(background(e,n)),$("bg").hide()})}var $slider=$(".slider");$slider.slick({arrows:!1,dots:!0,infinite:!0,speed:600,fade:!0,focusOnSelect:!0,customPaging:function(e,n){var r=$(e.$slides[n]).data("color").split(",")[1];return'<a><svg width="100%" height="100%" viewBox="0 0 16 16"><circle cx="8" cy="8" r="6.215" stroke="'+r+'"></circle></svg><span style="background:'+r+'"></span></a>'}}).on("beforeChange",function(e,n,r,a){console.log("beforeChange");var i=$("figure",$slider).eq(a).data("color").split(",");console.log(i),changeBg(i[0],i[1])});